import React from 'react';
import {
    Grid,
    Typography,
    Card,
    CardContent,
    CardMedia,
    CardActions,
    CardActionArea,
    Button
} from "@material-ui/core";

import wclnImage from '../assets/bike_profile.jpg';

import { theme } from "../theme";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
    cardImage: {
        height: 140
    },
}));

const ProjectList = () => {

    const classes = useStyles(theme);

    return (
        <Grid container justify="center" alignItems="center" spacing={3}>

            <Grid item xs={12} sm={6} md={3}>
                <Card>
                    <CardActionArea>
                        <CardMedia 
                            className={classes.cardImage}
                            image={wclnImage}
                            title="WCLN LTI provider"
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                WCLN LTI Provider solution
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                I built a custom LTI provider solution for Western Canadian Learning Network to distribute
                                course content to school districts throughout Western Canada. It handles millions of resource
                                requests every year.
                            </Typography>
                        </CardContent>
                    </CardActionArea>
                    <CardActions>
                        <Button size="small" color="secondary">
                            View on GitHub
                        </Button>
                    </CardActions>
                </Card> 
            </Grid>


        </Grid>
    );
}

export default ProjectList;
