import React, { Component } from 'react';
import {
    Grid,
    Typography,
    Box,
    FormControl,
    TextField,
    InputLabel,
    Input,
    Paper,
} from "@material-ui/core"; 

import { theme } from "../theme";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
    profileImage: {
        width: "90%",
        maxWidth: "500px"
    },
    root: {
        flexGrow: 1,
    },
    aboutSection: {
        marginBottom: "1em",
    },
}));

const Contact = () => {
    const classes = useStyles(theme);

    const handleSubmit = (e) => {
        e.preventDefault();
        window.console.log('Submitted!!!');
    };


    return (
        <Grid container direction="column" justify="center" alignItems="center">
            <Grid item xs={12}>
                <Box mb={2}>
                    <Typography variant="h2">Contact</Typography>
                </Box>
            </Grid>
            <Grid item xs={12}>
                <form onSubmit={handleSubmit}>
                    <Paper p={4}>
                        <TextField name="name" type="text" label="Your name" required />
                        <input name="email" type="text" label="Your email address" required />
                        <textarea name="message" rows="10" cols="70" label="Message"></textarea>
                        <input type="submit" name="submit"/>
                    </Paper>
                </form>
            </Grid>
        </Grid>
    );
}

export default Contact;
